import React, { useEffect, useState } from 'react';

const moods = [
  { icon: '😊', title: 'Happy' },
  { icon: '😐', title: 'Neutral' },
  { icon: '😌', title: 'Calm' },
  { icon: '😫', title: 'Stressed' },
  { icon: '😡', title: 'Angry' }
];

export default function Popup() {
  const [username, setUsername] = useState('Friend');
  const [currentMood, setCurrentMood] = useState('😊');
  const [moodHistory, setMoodHistory] = useState([]);
  const [journalPreview, setJournalPreview] = useState('Your journal entries will appear here');

  useEffect(() => {
    const name = localStorage.getItem('zenly-username') || 'Friend';
    setUsername(name);

    const moodData = JSON.parse(localStorage.getItem('zenly-mood-history')) || [];
    if (moodData.length > 0) {
      setCurrentMood(moodData[0].mood);
    }
    setMoodHistory(moodData);

    const journalEntries = JSON.parse(localStorage.getItem('zenly-journal-entries')) || [];
    if (journalEntries.length > 0) {
      setJournalPreview(journalEntries[0].content);
    }
  }, []);

  const updateMood = (mood) => {
    const reason = prompt(`You selected ${mood}. Can you tell us why you're feeling this way?`);
    if (reason !== null) {
      const newEntry = {
        mood,
        reason: reason || 'No reason provided',
        date: new Date().toLocaleString()
      };
      const updatedHistory = [newEntry, ...moodHistory].slice(0, 7);
      localStorage.setItem('zenly-mood-history', JSON.stringify(updatedHistory));
      setMoodHistory(updatedHistory);
      setCurrentMood(mood);

      chrome.runtime.sendMessage({
        type: 'moodUpdate',
        mood,
        reason,
        timestamp: Date.now()
      });
    }
  };

  const clearHistory = () => {
    localStorage.removeItem('zenly-mood-history');
    setMoodHistory([]);
  };

  return (
    <div className="container w-[350px] h-[500px] p-2 box-border bg-[#f8fafc]">
      <div className="w-full h-full bg-white rounded-xl shadow-lg flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white p-4 border-b">
          <div className="flex justify-between items-center">
            <h1 className="text-xl font-bold logo" style={{ fontFamily: 'Brody Script, cursive' }}>Zenly</h1>
            <div className="flex space-x-2">
              <button
                onClick={() => chrome.tabs.create({ url: 'https://yourwebsite.com/login' })}
                className="text-sm bg-blue-50 text-blue-600 px-3 py-1 rounded-lg hover:bg-blue-100"
              >
                Login
              </button>
              <button className="p-1 rounded-full hover:bg-gray-100" title="Settings">⚙️</button>
              <button className="p-1 rounded-full hover:bg-gray-100" title="Help">❓</button>
            </div>
          </div>
        </header>

        {/* Main content */}
        <main className="flex-1 overflow-y-auto p-4">
          <div className="mb-4">
            <h2 className="text-lg font-semibold">Hello, <span className="text-blue-600">{username}</span>!</h2>
            <p className="text-sm text-gray-500">How are you feeling today?</p>
          </div>

          {/* Mood Tracker */}
          <div className="bg-white rounded-lg shadow p-4 mb-4">
            <h3 className="font-medium mb-2">Select Your Current Mood</h3>
            <div className="flex justify-between text-2xl mb-3">
              {moods.map(({ icon, title }) => (
                <span
                  key={icon}
                  className="cursor-pointer transition-transform transform hover:scale-125"
                  title={title}
                  onClick={() => updateMood(icon)}
                >
                  {icon}
                </span>
              ))}
            </div>
            <div className="text-center text-3xl py-2">{currentMood}</div>
          </div>

          {/* Mood History */}
          <div className="bg-white rounded-lg shadow p-4 mb-4">
            <div className="flex justify-between items-center mb-2">
              <h3 className="font-medium">Your Mood History</h3>
              <button onClick={clearHistory} className="text-red-500 text-xs hover:underline">Clear</button>
            </div>
            <div className="max-h-[120px] overflow-y-auto text-sm">
              {moodHistory.length === 0 ? (
                <div className="text-gray-500 italic">Your mood history will appear here</div>
              ) : (
                moodHistory.map((entry, idx) => (
                  <div key={idx} className="py-2 border-b border-gray-100">
                    <div className="flex justify-between mb-1">
                      <span className="text-xl">{entry.mood}</span>
                      <span className="text-xs text-gray-500">{entry.date}</span>
                    </div>
                    <div className="text-xs text-gray-600 truncate" title={entry.reason}>{entry.reason}</div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Quick Actions */}
          <div className="grid grid-cols-2 gap-3 mb-4">
            <button
              onClick={() => document.getElementById('journal-preview')?.scrollIntoView({ behavior: 'smooth' })}
              className="bg-blue-50 text-blue-600 p-3 rounded-lg flex flex-col items-center"
            >
              <span className="text-2xl mb-1">📔</span>
              <span className="text-sm">Journal</span>
            </button>
            <button
              onClick={() => chrome.tabs.create({ url: chrome.runtime.getURL('home.html#chat') })}
              className="bg-purple-50 text-purple-600 p-3 rounded-lg flex flex-col items-center"
            >
              <span className="text-2xl mb-1">💬</span>
              <span className="text-sm">Chat</span>
            </button>
          </div>

          {/* Journal Section */}
          <div className="bg-white rounded-lg shadow p-4 mb-4">
            <div className="flex justify-between items-center mb-2">
              <h3 className="font-medium">Journal</h3>
              <button className="text-blue-500 text-sm">View All</button>
            </div>
            <div className="max-h-[100px] overflow-y-auto text-sm text-gray-600 mb-2">
              <p id="journal-preview">{journalPreview}</p>
            </div>
          </div>

          {/* Emergency Resources */}
          <details className="bg-red-50 rounded-lg overflow-hidden mb-4">
            <summary className="p-3 text-red-600 font-medium cursor-pointer flex justify-between items-center">
              <span>Emergency Resources</span>
              <span className="text-lg">▼</span>
            </summary>
            <div className="p-3 pt-0">
              <a href="#" className="block text-sm text-red-600 py-1 hover:underline">Crisis Hotline</a>
              <a href="#" className="block text-sm text-red-600 py-1 hover:underline">Breathing Exercises</a>
              <a href="#" className="block text-sm text-red-600 py-1 hover:underline">Grounding Techniques</a>
            </div>
          </details>
        </main>
      </div>
    </div>
  );
}
